package com.forloopackage;

public class ForAlphabet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch, ch2;
		
		for(ch = 'A'; ch<='Z'; ch++)
		{
			System.out.println(ch+"\n");
		}
			for(ch2 = 'a'; ch2<='z'; ch2++)
			{
				System.out.println(ch2);
			}
			
	}

}
