package com.series;

public class SerisOf2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,exp=4,base=2,power=1;
		
		System.out.println(power);
		for(i=1;i<=exp;i++)
		{
			power=power*base;
			power=power+1;
			//System.out.println(i+" base "+base+" power "+power);
			System.out.println(power);
		}
		
		
	}

}
